mapper.select:
SELECT admin_no, admin_id, admin_passwd
FROM admin
WHERE admin_no = #{admin_no}

SELECT COUNT(admin_no) as cnt
FROM admin
WHERE consumer_no = #{consumer_no}


  SELECT admin_no, consumer_no
  FROM admin
  WHERE consumer_no = #{consumer_no}

mapper.update:
UPDATE admin
SET  admin_id = #{admin_id}, admin_passwd= #{admin_passwd}
WHERE admin_no = #{admin_no}

mapper.select:
  SELECT COUNT(*) as cnt
	FROM admin
	WHERE consumer_no = #{consumer_no}
